  		if(ads[i]) 
  			adsView+=ads[i]; 
  	} 
  	document.getElementById('adsContainer').innerHTML = adsView; 
  } 
  function JsonToAds(r){ 
  	for(i=0;i<r.length;i++) 
  	{ 
  	if (r[i]['title'] != "") { 
  		ads[i + k] = '<div class="advBlock" onmousemove="_mv(this);"><a class="advHeader" onmousedown="_md(this);" href="' + r[i]['url'] + '&p=h&m=0&h=0" target="_blank" >' + r[i]['title'] + 
  		'</a><a class="advDescription" onmousedown="_md(this);" href="' + 
  		r[i]['url'] + 
  		'&p=d&m=0&h=0" target="_blank" >' + 
  		r[i]['description'] + 
  		'</a><a class="advCost" onmousedown="_md(this);" href="' + 
  		r[i]['url'] + 
  		'&p=p&m=0&h=0" target="_blank" >' + 
  		r[i]['price'] + 
  		'</a><a href="' + 
  		r[i]['url'] + 
  		'&p=i&m=0&h=0" target="_blank" onmousedown="_md(this);" ><img class="advImage" src="' + 
  		r[i]['image'] + 
  		'" alt="' + 
  		r[i]['title'] + 
  		'"></a></div>'; 
  		guids += r[i]['id'] + "_"; 
  		imgs.src = r[i]['image']; 
  	} 
  	else 
  	k-=1; 
  	} 
  	 k=ads.length; 
  
  	if(k>=t*count)k=t*count; 
  } 
  function getXmlHttp(){ 
  	try { 
  		return new ActiveXObject("Msxml2.XMLHTTP"); 
  	} catch (e) { 
          try { 
              return new ActiveXObject("Microsoft.XMLHTTP"); 
          } catch (ee) { } 
      } 
      if (typeof XMLHttpRequest!='undefined') { 
          return new XMLHttpRequest(); 
  
      } 
  } 
  
  function pre_load(){ 
  	var params = 'exclude=' + encodeURIComponent(guids) + "&rand=" + Math.floor(Math.random() * 1000000); 
  	var xmlhttp = getXmlHttp(); 
  	xmlhttp.open("GET", "/adshow.fcgi?scr=64c96d46-0128-11e4-b33b-002590d8e030&show=json&"+params, true) 
  
  	xmlhttp.onreadystatechange=function(){ 
    if (xmlhttp.readyState != 4) return 
  
    clearTimeout(timeout) 
  
    if (xmlhttp.status == 200) { 
  	eval("var responsedata = (" + xmlhttp.responseText + ")"); 
  	if (responsedata.length > 0) { 
  		JsonToAds(responsedata); 
  		showAds(cursor, cursor + count); 
  		_sem = true; 
  		cursor += count; 
  		if (cursor > count) 
  			document.getElementById('nav_r').style.display = 'block'; 
  		if ((cursor) >= t * count) { 
  			document.getElementById('nav_l').style.display = 'none'; 
  		} 
  		else 
  			document.getElementById('nav_l').style.display = 'block'; 
  	} 
    } 
    else { 
  	handleError("Опаньки"); 
    } 
  } 
  
  xmlhttp.send(null); 
  
  var timeout = setTimeout( function(){ xmlhttp.abort(); handleError("Time over") },10000); 
  function handleError(message){if(ads.length>0) return; /*document.getElementById('nav_l').style.display='block';*/document.getElementById('adsContainer').innerHTML ="<div>Опаньки!<br />Загрузка данных прервана:(</div>";pre_load();} 
  
  } 
  
  function next(){ 
   document.getElementById('nav_l').style.display='none'; 
  	adsView=""; 
  
  	if (true || ads.length < t * count && cursor >= ads.length) { 
  		pre_load(); 
  	} 
  	else 
  	{ 
  		if (cursor >= count) 
  			document.getElementById('nav_l').style.display = 'none'; 
  		else 
  			document.getElementById('nav_l').style.display = 'block'; 
  
  		if ((cursor) >= ads.length) { 
  			cursor = ads.length - count; 
  			document.getElementById('nav_l').style.display = 'none'; 
  		} 
  
  		showAds(cursor,cursor + count); 
  		cursor += count; 
  		document.getElementById('nav_r').style.display = 'block'; 
  		_sem = false; 
  		_click = false; 
  	} 
  } 
  function prev(){ 
  	adsView=""; 
  	cursor-=count; 
  	if(cursor-count<0)cursor=0+count; 
  	var start=cursor-count; 
  	if(start<0)start=0; 
  	showAds(start,start+count); 
  	if(count>=cursor)document.getElementById('nav_r').style.display='none'; 
  	if(cursor<ads.length)document.getElementById('nav_l').style.display='block'; 
  } 
  function change(cl){ 
  	var divs = document.getElementsByTagName("DIV"); 
  	for (var i = 0; i < divs.length; i++) { 
  		if (divs[i].className == cl) 
  		divs[i].className=cl+'2'; 
  	} 
  } 
  
  function change1(cl){ 
  	var divs = document.getElementsByTagName("DIV"); 
  	for (var i = 0; i < divs.length; i++) { 
  		if (divs[i].className == cl+"2") 
  			divs[i].className=cl; 
  		if (divs[i].className == cl + "2") 
  			divs[i].className=cl+'1'; 
  	} 
  } 
function _mv(obj){
	_m=true;
}
function _md(obj){
if(_m) obj.setAttribute('href',obj.getAttribute('href').replace('&m=0','&m=1'));obj.setAttribute('href',obj.getAttribute('href').replace('&h=0','&h=1'));
}  </script> </body>
